mapper.update:
update tmp_board
set bdContent=#{content},
where bdTitle=#{title}

mapper.insert:
insert into tmp_content(
	price
	, apartName
	, floor
	, area
	, date
	, address 
) 
values(
	#{price}
	, #{apartName}
	, #{floor}
	, #{area}
	, #{date}
	, #{address}
)

insert into tmp_rereply(
	bno
	, writer
	, contents
	, date
	, originNo
	, groupOrd
	, groupLayer
)
values(
	#{bno}
	, #{writer}
	, #{contents}
	, now()
	, #{originNo}
	, #{groupOrd}
	, #{groupLayer}
)

mapper.update:
update tmp_rereply
set contents=#{contents}
where rno=#{rno}
